import React, { Component } from 'react';
import axios from 'axios';
import { Container, Row, Col } from "react-bootstrap";
import { useAuth0 } from "@auth0/auth0-react";

import PatientForm from './PatientForm';
import DoctorForm from './DoctorForm';
import InsuranceForm from './InsuranceForm';

function UserForm() {
    const { user } = useAuth0();
    const { name, picture, email } = user;

    const { getAccessTokenSilently } = useAuth0();
    const token = getAccessTokenSilently();
    const [state, setState] = React.useState({
        role: 'patient',
        first_name: '',
        last_name: '',
		user_email: {email},
		insurance: 'insurance provider',
        street_name: 'street name',
    	city: 'city',
    	state_name: 'state',
    	zip_code: 'zip code'
    });

    const handleChange = (event) => {
        setState({
            ...state,
            role: event.target.value
        });
    }

    return (
        <Container className="mb-5">
            <Row className="align-items-center profile-header mb-5 text-center text-md-left border-bottom border-dark">
                <Col md={2}>
                <img
                    src={picture}
                    alt="Profile"
                    className="rounded-circle img-fluid profile-picture mb-3 mb-md-0"

                />
                </Col>
                <Col md>
                <h2>{name}</h2>
                <p className="lead text-muted">{email}</p>

                <label for="roles">Choose a role:</label>
                <select name="roles" id="roles" onChange={handleChange}>
                    <option value="patient">Patient</option>
                    <option value="doctor">Doctor</option>
                    <option value="insurance">Insurance</option>
                </select>
                </Col>
            </Row>

            <Row className="padding-bottom mb-5">
                <h4>Account Information</h4>
            </Row>

            {state.role == 'patient' && 
                <PatientForm />
            }

            {state.role == 'doctor' && 
                <DoctorForm />
            }

            {state.role == 'insurance' && 
                <InsuranceForm />
            }

        </Container>
    )
}

export default UserForm;
