import React, { Component } from 'react';
import axios from 'axios';
import { Container, Row, Col } from "react-bootstrap";
import { useAuth0 } from "@auth0/auth0-react";

// import PatientForm from './PatientForm';
// import DoctorForm from './DoctorForm';
// import InsuranceForm from './InsuranceForm';

function UserForm() {
    const { user } = useAuth0();
    const { name, picture, email } = user;

    const { getAccessTokenSilently } = useAuth0();
    const token = getAccessTokenSilently();
    const [state, setState] = React.useState({
        role: 'patient',
        first_name: '',
        last_name: '',
		user_email: {email},
		insurance: 'insurance provider',
        street_name: 'street name',
    	city: 'city',
    	state_name: 'state',
    	zip_code: 'zip code'
    });

    const handleRoleChange = (event) => {
        setState({
            ...state,
            role: event.target.value
        });
    }

    const handleSubmit = (event) => {
        event.preventDefault();
    }

    return (
        <Container className="mb-5">
            <Row className="align-items-center profile-header mb-5 text-center text-md-left border-bottom border-dark">
                <Col md={2}>
                <img
                    src={picture}
                    alt="Profile"
                    className="rounded-circle img-fluid profile-picture mb-3 mb-md-0"

                />
                </Col>
                <Col md>
                <h2>{name}</h2>
                <p className="lead text-muted">{email}</p>

                <label for="roles">Choose a role: </label>
                <select name="roles" id="roles" onChange={handleRoleChange}>
                    <option value="patient">Patient</option>
                    <option value="doctor">Doctor</option>
                    <option value="insurance">Insurance</option>
                </select>
                </Col>
            </Row>

            <Row className="padding-bottom mb-5">
                <h4>Account Information</h4>
            </Row>

            {state.role == 'patient' && 
                <Row>
                    Patient <br /><br />

                    <form onSubmit={handleSubmit}>
                        <label for="first_name">First name:</label><br />
                        <input type="text" name="first_name" value="John" /><br />

                        <label for="last_name">Last name:</label><br />
                        <input type="text" name="last_name" value="Doe" /><br />

                        <input type="submit" value="Submit" />
                    </form>
                </Row>
            }

            {state.role == 'doctor' && 
                <Row>
                    Doctor <br />

                    <form onSubmit={handleSubmit}>

                    </form>
                </Row>
            }

            {state.role == 'insurance' && 
                <Row>
                    Insurance <br />

                    <form onSubmit={handleSubmit}>

                    </form>
                </Row>
            }

        </Container>
    )
}

export default UserForm;
